$(function () {
	//SMS
	c3.generate({
		bindto: "#sms",
		data: {
			columns: [["SMS Consumido", 30]],
			type: "gauge",
			tooltip: {show: !0}
		},
		donut: {
			label: {show: !1},
			title: "Sales",
			width: 18
		},
		legend: {hide: !0},
		color: {pattern: ["#5f76e8"]}
	});

	d3.select("#sms .c3-chart-arcs-title").style("font-family", "Rubik");
	
	//Chamadas 
	c3.generate({
		bindto: "#chamadas",
		data: {
			columns: [["Chamadas", 100]],
			type: "gauge",
			tooltip: {show: !0}
		},
		donut: {
			label: {show: !1},
			title: "Sales",
			width: 18
		},
		legend: {hide: !0},
		color: {pattern: ["#5f76e8"]}
	});

	d3.select("#chamadas .c3-chart-arcs-title").style("font-family", "Rubik");
	
	//Chamadas Excedentes
	c3.generate({
		bindto: "#chamadasex",
		data: {
			columns: [["Chamadas Excedentes", 10]],
			type: "gauge",
			tooltip: {show: !0}
		},
		donut: {
			label: {show: !1},
			title: "Sales",
			width: 18
		},
		legend: {hide: !0},
		color: {pattern: ["#5f76e8"]}
	});

	d3.select("#chamadasex .c3-chart-arcs-title").style("font-family", "Rubik");

	//Barras SMS
	var datasms = {
		labels: ["Jan", "Fev", "Mar", "Abr", "Mai", "Jun", "Jul", "Ago", "Set", "Out", "Nov", "Dez"],
		series: [[5, 4, 3, 7, 5, 10, 5, 4, 3, 7, 5, 10]]
	  };
	  
	var optionssms = {
		seriesBarDistance: 30
	};
	var responsiveOptionssms = [
		['screen and (max-width: 640px)', {
		  seriesBarDistance: 5,
		  axisX: {
			labelInterpolationFnc: function (value) {
			  return value[0];
			}
		  }
		}]
	  ];
	  
	  new Chartist.Bar('#graphsms', datasms, optionssms, responsiveOptionssms);

	  //Barras Chamadas
	  var datachamada = {
		labels: ["Jan", "Fev", "Mar", "Abr", "Mai", "Jun", "Jul", "Ago", "Set", "Out", "Nov", "Dez"],
		series: [[5, 4, 3, 7, 5, 10, 5, 4, 3, 7, 5, 10]]
	  };
	  
	  var optionschamada = {
		seriesBarDistance: 30
	  };
	  var responsiveOptionschamada = [
		['screen and (max-width: 640px)', {
		  seriesBarDistance: 5,
		  axisX: {
			labelInterpolationFnc: function (value) {
			  return value[0];
			}
		  }
		}]
	  ];
	  
	  new Chartist.Bar('#graphchamada', datachamada, optionschamada, responsiveOptionschamada);
	
	$(window).on("resize", function () {
		t.update()
	})
});